   static func populateCategories(for cards: [Card]) -> [Card] {
      var idToCategories: [Int: [String]] = [:]
      
      for (category, ids) in DummyData.defaultCardsDictionary {
         for id in ids {
            if idToCategories[id] == nil {
               idToCategories[id] = []
            }
            idToCategories[id]?.append(category)
         }
      }
      
      return cards.map { card in
         let newCard = Card(id: card.id, keyWord: card.keyWord, forbiddenWords: card.forbiddenWords)
         newCard.categories = idToCategories[card.id] ?? []
         return newCard
      }
   }
   
   
}


